{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"name":"python","version":"3.10.14","mimetype":"text/x-python","codemirror_mode":{"name":"ipython","version":3},"pygments_lexer":"ipython3","nbconvert_exporter":"python","file_extension":".py"},"kaggle":{"accelerator":"none","dataSources":[{"sourceId":2431952,"sourceType":"datasetVersion","datasetId":1471681},{"sourceId":4652263,"sourceType":"datasetVersion","datasetId":2607913},{"sourceId":7636187,"sourceType":"datasetVersion","datasetId":4449920},{"sourceId":7722355,"sourceType":"datasetVersion","datasetId":4511073},{"sourceId":7725309,"sourceType":"datasetVersion","datasetId":4484379},{"sourceId":9250157,"sourceType":"datasetVersion","datasetId":5596118}],"dockerImageVersionId":30761,"isInternetEnabled":false,"language":"python","sourceType":"script","isGpuEnabled":false}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code] {\"execution\":{\"iopub.status.busy\":\"2025-01-25T15:31:51.414363Z\",\"iopub.execute_input\":\"2025-01-25T15:31:51.414777Z\",\"iopub.status.idle\":\"2025-01-25T15:31:51.819753Z\",\"shell.execute_reply.started\":\"2025-01-25T15:31:51.414729Z\",\"shell.execute_reply\":\"2025-01-25T15:31:51.818571Z\"},\"jupyter\":{\"outputs_hidden\":false}}\n# This Python 3 environment comes with many helpful analytics libraries installed\n# It is defined by the kaggle/python Docker image: https://github.com/kaggle/docker-python\n# For example, here's several helpful packages to load\n\nimport numpy as np # linear algebra\nimport pandas as pd # data processing, CSV file I/O (e.g. pd.read_csv)\n\n# Input data files are available in the read-only \"../input/\" directory\n# For example, running this (by clicking run or pressing Shift+Enter) will list all files under the input directory\n\nimport os\n# for dirname, _, filenames in os.walk('/kaggle/input'):\n#     for filename in filenames:\n#         print(os.path.join(dirname, filename))\n\n# You can write up to 20GB to the current directory (/kaggle/working/) that gets preserved as output when you create a version using \"Save & Run All\" \n# You can also write temporary files to /kaggle/temp/, but they won't be saved outside of the current session\n\n# %% [markdown] {\"jupyter\":{\"outputs_hidden\":false}}\n# # Object Detection using multiple class dataset\n\n# %% [markdown] {\"jupyter\":{\"outputs_hidden\":false}}\n# Cold Drinks Inventory Dataset\n\n# %% [markdown] {\"jupyter\":{\"outputs_hidden\":false}}\n# Test Demo Version\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2025-01-25T15:31:51.822597Z\",\"iopub.execute_input\":\"2025-01-25T15:31:51.823206Z\",\"iopub.status.idle\":\"2025-01-25T15:31:52.841140Z\",\"shell.execute_reply.started\":\"2025-01-25T15:31:51.823155Z\",\"shell.execute_reply\":\"2025-01-25T15:31:52.839981Z\"},\"jupyter\":{\"outputs_hidden\":false}}\nimport pandas as pd\nimport numpy as np\nimport matplotlib.pyplot as plt\nimport cv2\nimport os\n\nimport pandas as pd\nimport cv2\nimport numpy as np\nimport os\nfrom sklearn.model_selection import train_test_split\n# %matplotlib inline\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2025-01-25T15:31:52.842604Z\",\"iopub.execute_input\":\"2025-01-25T15:31:52.843243Z\",\"iopub.status.idle\":\"2025-01-25T15:31:52.955167Z\",\"shell.execute_reply.started\":\"2025-01-25T15:31:52.843186Z\",\"shell.execute_reply\":\"2025-01-25T15:31:52.953978Z\"},\"jupyter\":{\"outputs_hidden\":false}}\nimport yaml\n\ndataset_config = {\n    'train': '/kaggle/input/cold-drinks-inventory-dataset/Finalize/images/train',  # Adjust this path\n    'val': '/kaggle/input/cold-drinks-inventory-dataset/Finalize/images/test',  # Adjust this path if you have a separate validation set\n    'nc': 6,\n    'names': ['Coca Cola',\n             'Sprite',\n             'Pepsi',\n             'Mountain Dew',\n             '7UP',\n             'Fanta']  # Adjust this list if you have more classes\n}\n\n# Specify the path where you want to save the YAML file\nyaml_path = '/kaggle/working/dataset.yaml'\n\n# Write the dictionary to a YAML file\nwith open(yaml_path, 'w') as yamlfile:\n    yaml.dump(dataset_config, yamlfile, default_flow_style=False)\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2025-01-25T15:31:52.956680Z\",\"iopub.execute_input\":\"2025-01-25T15:31:52.957125Z\"},\"jupyter\":{\"outputs_hidden\":false}}\n# !git clone https://github.com/ultralytics/yolov5  # Clone YOLOv5 repository\n# %cd yolov5\n# !pip install -r requirements.txt  # Install required dependencies\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false}}\n# !python train.py --img 640 --batch 16 --epochs 100 --data /kaggle/working/dataset.yaml --cfg ./models/yolov5s.yaml --weights yolov5s.pt --name yolo_custom\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false}}\n# pip install torch torchvision\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false}}\nimport torch\nimport cv2\nimport matplotlib.pyplot as plt\nimport numpy as np\n\ndef detect_and_visualize(model_path, image_path, conf_threshold=0.3, iou_threshold=0.45):\n    # Load the model\n    model = torch.hub.load('ultralytics/yolov5', 'custom', path=model_path, force_reload=True)\n    \n    # Set confidence and IoU thresholds\n    model.conf = conf_threshold\n    model.iou = iou_threshold\n    \n    # Perform inference\n    results = model(image_path)\n    \n    # Process results to extract labels and bounding box coordinates\n    labels, cords = results.xyxyn[0][:, -1].cpu().numpy(), results.xyxyn[0][:, :-1].cpu().numpy()\n    \n    # Load image using OpenCV and convert it to RGB format\n    img = cv2.imread(image_path)\n    img = cv2.cvtColor(img, cv2.COLOR_BGR2RGB)\n    \n    # Dictionary to store counts of each label\n    label_counts = {}\n    \n    # Draw bounding boxes and labels on the image\n    for cord in cords:\n        # Ensure bbox contains x1, y1, x2, y2, conf\n        if len(cord) == 5:\n            x1, y1, x2, y2, conf = cord\n            if conf >= conf_threshold:\n                start = (int(x1 * img.shape[1]), int(y1 * img.shape[0]))\n                end = (int(x2 * img.shape[1]), int(y2 * img.shape[0]))\n                cv2.rectangle(img, start, end, (255, 0, 0), 2)\n                label = int(labels[np.where(cords[:, :4] == cord[:4])[0][0]])  # Extract label\n                cv2.putText(img, f'{label} {conf:.2f}', start, cv2.FONT_HERSHEY_SIMPLEX, 0.9, (255, 255, 255), 2)\n                # Update label count\n                label_counts[label] = label_counts.get(label, 0) + 1\n    \n    # Visualization using matplotlib\n    plt.figure(figsize=(20, 10))  # Adjusted for better fit on standard screens\n    plt.imshow(img)\n    plt.axis('off')  # Hides the axis to focus on the image\n    plt.show()\n\n    # Print the count of detected objects\n    print(f'Detected objects: {len(cords)}')\n    \n    # Print count of each label\n    for label, count in label_counts.items():\n        print(f'Label {label}: {count}')\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false}}\n# Example usage\n%matplotlib inline\nmodel_path = '/kaggle/input/previous-version-output/yolov5/runs/train/yolo_custom/weights/best.pt'  # Update this path\nimage_path = '/kaggle/input/testdrinks/image_2024_02_23T12_06_03_052Z.png'\ndetect_and_visualize(model_path, image_path)\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false}}\nimage_path = '/kaggle/input/cold-drinks-inventory-dataset/Finalize/images/train/0bbc8618-225e-4b2a-9fe1-571af5938737.jpg'\n\ndetect_and_visualize(model_path, image_path)\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false}}\nimage_path = '/kaggle/input/testdrinks/image_2024_02_23T12_12_26_816Z.png'\n\ndetect_and_visualize(model_path, image_path)\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false}}\nimage_path = '/kaggle/input/testdrinks/image_2024_02_23T12_11_59_469Z.png'\n\ndetect_and_visualize(model_path, image_path)\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false}}\nimage_path = '/kaggle/input/testdrinks/im_two.jpg'\n\ndetect_and_visualize(model_path, image_path)\n\n# %% [code] {\"_kg_hide-input\":false,\"jupyter\":{\"outputs_hidden\":false}}\n","metadata":{"_uuid":"64c93d90-09f8-40db-a8ee-7b138c67d91d","_cell_guid":"57281520-bb79-45bd-bf80-82e63d950b33","trusted":true,"collapsed":false,"jupyter":{"outputs_hidden":false}},"outputs":[],"execution_count":null}]}